import '../App.css'
import * as location from './1055-world-location.json'

function Loading() {
    const locationOptions = {
        loop: true,
        autoplay: true, 
        animationData: location.default,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
      }
